import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import './App.css';

const REST_API_CALCULATOR_URL = "http://localhost:8080/api/submit";

export default function App() {
    const [targetAmount, setTargetAmount] = useState(0);
    const [coinDenominators, setCoinDenominators] = useState([]); 
    const [coinsNeeded, setCoinsNeeded] = useState([]); 

    function handleInputChange(e) {
        const { name, value } = e.target;
        if (name === "targetAmount") {
            setTargetAmount(value);
        } else if (name === "coinDenominators") {
            setCoinDenominators(value);
        }
    }

    function handleClick(e) {
		e.preventDefault();
        console.log("Confirm button is clicked");
        console.log("Target Amount:", targetAmount);
		console.log("Coin Denominators:", coinDenominators);

        const requestData = {
            targetAmount: parseFloat(targetAmount), 
            coinDenominations: coinDenominations.split(/[\s,]+/).map(Number),
        }

        axios
            .post(REST_API_CALCULATOR_URL, requestData, {
                headers: {
                    "Content-Type": "application/json",
                },
                withCredentials: true,
            })
            .then((response) => {
                console.log("Response data:", response.data);
                setCoinsNeeded(response.data);
            })
            .catch((error) => {
                console.error("Error sending request data:", error);
            });
        }

    return (
        <div classname="container">
            <div classname="header">
            <p>Which Coins Do You Need?</p>
            </div>
            <form>
                <label classname="label" htmlFor="targetAmount">Target Amount: </label>
				    <input classname="input"
					    type="number"
					    name="targetAmount"
					    placeholder="Within the range between 0 to 10,000.00 up to 2 decimal places"
                        value={targetAmount}
					    onChange={handleInputChange}    />
				<br />
				<label classname="label" htmlFor="coinDenominators">Coin Denominators: </label>
				    <input classname="input"
					    type="text"
					    name="coinDenominators"
					    placehlder="Accepted values [0.01, 0.05, 0.1, 0.2, 0.5, 1, 2, 5, 10, 50, 100, 1000]"
                        value={coinDenominators}
					    onChange={handleInputChange}    />
                <br />
				<button classname="button" onClick={handleClick}>Calculate</button>
			</form>
            <br />
            <br />
            <div classname="header">
            <p>Results</p>
            </div>
            <form>
            <label classname="label" htmlFor="coinsNeeded">Coins Needed: </label>
				    <input classname="input"
					    type="text"
					    name="coinsNeeded"
					    value={coinsNeeded.join(", ")}    />
            </form>
        </div>
);
}
